mapper.select:
SELECT COUNT(*)
FROM CART
WHERE MEMBER_NO = #{memberNo}
AND PRODUCT_NO = #{productNo}

SELECT PRODUCT_AMOUNT
FROM CART
WHERE MEMBER_NO = #{memberNo}
AND PRODUCT_NO = #{productNo}

mapper.update:
UPDATE CART SET 
PRODUCT_AMOUNT = #{productAmount}
WHERE MEMBER_NO = #{memberNo}
AND PRODUCT_NO = #{productNo}

UPDATE CART SET 
PRODUCT_AMOUNT = PRODUCT_AMOUNT +1
WHERE MEMBER_NO = #{memberNo}
AND PRODUCT_NO = #{productNo}

UPDATE CART SET 
PRODUCT_AMOUNT = PRODUCT_AMOUNT -1
WHERE MEMBER_NO = #{memberNo}
AND PRODUCT_NO = #{productNo}

mapper.delete:
DELETE FROM CART
WHERE MEMBER_NO = #{memberNo}
AND PRODUCT_NO = #{productNo}
